fn main() {
	let a: &str = "";
	let b: &str = "";
	println!("{}",ham(&a,&b));
}

fn ham(s: &str, t: &str) -> i32 {
	let n: i32 = *&s.len() as i32;
	let mut count: i32 = 0;
	let s1: Vec<_> = s.chars().collect();
	let s2: Vec<_> = t.chars().collect();
	for i in 0..n {
		if s1[i as usize] != s2[i as usize] {
			count += 1;
		}
	}
	count
}